import React from 'react';
import PropTypes from 'prop-types';
import './controlsItem.scss';

function Car(props) {
    return <li>I am a { props.brand }</li>;
  }

  function Garage() {
    const cars = [
      {id: 1, brand: 'Ford'},
      {id: 2, brand: 'BMW'},
      {id: 3, brand: 'Audi'}
    ];
    return (
      <>
          <h1>Who lives in my garage?</h1>
          <ul>
          {cars.map((car) => <Car key={car.id} brand={car.brand} />)}
        </ul>
      </>
    );
}

const ControlsItem = ({ text, backgroundColor, ...props}) => {
         return(
             <div>
                <div className="bg-color" style={backgroundColor && { backgroundColor }}
                    {...props}>
                    { text }
                </div>
                <Car />

             </div>

         );
}

ControlsItem.propTypes = {
    text: PropTypes.string,
    backgroundColor: PropTypes.string,
};

ControlsItem.defaultProps = {
    text: "hello world",
    backgroundColor: '#555555',
};

export default ControlsItem;
