import React from 'react';
import PropTypes from 'prop-types';
import './controlsItem.scss';

const ControlsItem = ({ text, backgroundColor, ...props}) => {
         return(
             <div className="bg-color" style={backgroundColor && { backgroundColor }}
             {...props}>
                 { text }
             </div>
         );
}

ControlsItem.propTypes = {
    text: PropTypes.string,
    backgroundColor: PropTypes.string,
};

ControlsItem.defaultProps = {
    text: "hello world",
    backgroundColor: "#000",
};

export default ControlsItem;
